export const BookComponent = () => {
  const [books, setBooks] = useState([
    { id: 1, title: 'React Basics', author: 'Jane Smith' },
    { id: 2, title: 'Learn JavaScript', author: 'John Doe' }
  ])

  const handleAddBook=()=>{
    const newBook={
      id:new Date(),
      title: 'Java',
      author: 'Ermira Kajtazi'
    }

    setBooks([...books,...newBook])
  }

  return (
    <div>
      {books.map((books) => (
        <>
          <div>{book.book}</div>
          <div>{book.author}</div>
        </>
      ))}
    </div>
  )
}
