import { useState } from 'react';
import './App.css';

function App() {

  const [hover, setHover] = useState(false);

  return (
    <>
    <div
      style= {{color: hover ? "red" : "black"}}
        onMouseOver={()=>setHover(true)}
        onMouseOut={()=>setHover(false)}
    >
      Future Minds Academy!</div>
    </>
  )
}

export default App;
