import { useState } from 'react';
import './App.css';

function App() {
  const [toggle, setToggle] = useState(false);

  return (
    <>
    <div style={{backgroundColor: toggle ? "black" : "white", color: toggle ? 'white' : 'black'}}>
    <button
    onClick={
      ()=> setToggle(!toggle)}
      >Toggle Theme</button>
      </div>
    </>
  )
}

export default App;
