import { useState } from 'react';
import './App.css';

function App() {

  const [toggle, setToggle] = useState('false');


  return (
    <>
    <button style={{backgroundColor: toggle ? "black" : "white"}} onClick={setToggle(prev =>!prev)}>Toggle Theme</button>
    </>
  )
}

export default App;
