import { useState } from 'react';
import './App.css';

function App() {

  const [color, setColor] = useState('black');

  return (
    <>
    <div onMouseOver={()=>setColor('red')}
      onMouseOut={()=>setColor('black')}
      >Future Minds Academy!</div>
    </>
  )
}

export default App;
