import { useState } from "react";

export const AddFruitForm = () => {
    const [newFruit, setNewFruit] = useState({
        name: '',
        color: '',
        taste: '',
        image: ''
    }
    )
    return (
        <div style={{ display: 'flex', margin:''}}>
            <input type="text"px
                placeholder="Fruit name"
                value={newFruit.name}
            />
            <input type="text"
                placeholder="Color"
                value={newFruit.name}
            />
            <input type="text"
                placeholder="Taste"
                value={newFruit.name}
            />
            <input type="text"
                placeholder="Image"
                value={newFruit.name}
            />
            <button>Add new fruit</button>
        </div>

    )
}
