import { useState } from "react";

export const AddFruitForm = () => {
    const [newFruit, setNewFruit] = useState({
        name: '',
        color: '',
        taste: '',
        image: ''
    })

    const handleSubmit=()=>{
        const fruitToAdd={
            id: Date.now(), 
            ...newFruit
        }
    }
    

    return (
        <div style={{ display: 'flex'}}>
            <input type="text"
                placeholder="Fruit name"
                value={newFruit.name}
            />
            <input type="text"
                placeholder="Color"
                value={newFruit.name}
            />
            <input type="text"
                placeholder="Taste"
                value={newFruit.name}
            />
            <input type="text"
                placeholder="Image"
                value={newFruit.name}
            />
            <button onClick={handleSubmit}>Add new fruit</button>
        </div>

    )
}
