import { useState } from "react";

export const AddFruitForm = ()=>{
    const [newFruit, setNewFruit]=useState({
        name: '',
        color: '',
        taste: '',
        image: ''
    }
    )
    return(
        <div style={{display:'flex'}}>
            <input type="text" 
            placeholder="Fruit name"
            value={newFruit.name}
            name={name}
            />
            <input type="text" 
            placeholder="Color"
            value={newFruit.name}
            name={color}
            />
            <input type="text" 
            placeholder="Taste"
            value={newFruit.name}
            name={taste}
            />
            <input type="text" 
            placeholder="Image"
            value={newFruit.name}
            name={image}
            />
            <button>Add new fruit</button>
        </div>

    )
}
