import { useState } from "react";

export const CardChildComponent = ({ fruit, isEditing, onEdit, onSave }) => {

  const [editFruit, setEditFruit]= useState({
    name: '',
        color: '',
        taste: '',
        image: ''
  })

  return (
    <div style={{
      backgroundColor: '#f9f9f9',
      padding: '16px',
      borderRadius: '12px',
      width: '250px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center'
    }}>
      {isEditing ?
        <>
          <input
            name="name"
            defaultValue={fruit.name}
            onChange={(event)=>event.target.value}
          />
          <input
            name="color"
            defaultValue={fruit.color}
            onChange={(event)=>event.target.value}
          />
          <input
            name="taste"
            defaultValue={fruit.taste}
            onChange={(event)=>event.target.value}
          />
          <input
            name="image"
            defaultValue={fruit.image}
            onChange={(event)=>event.target.value}
          />
          <button onClick={() => onSave(fruit.id) }>Save</button>
        </>
        :
        //kodi per edit
        <>
          <img
            src={fruit.image}
            alt={fruit.name}
            style={{
              width: '100%',
              height: '150px',
              objectFit: 'contain',
              borderRadius: '8px',
              marginBottom: '12px'
            }}
          />
          <h3>{fruit.name}</h3>
          <p style={{ color: '#222' }}><strong>Color:</strong> {fruit.color}</p>
          <p style={{ color: '#222' }}><strong>Taste:</strong> {fruit.taste}</p>
        </>
      }
    </div>
  );
};
