      options: ["London", "Berlin", "Paris", "Madrid"],
      answer: 2  // index of "Paris"
    },
    {
      question: "What color is the sky?",
      options: ["Green", "Blue", "Red", "Yellow"],
      answer: 1  // index of "Blue"
    },
    {
      question: "How many legs does a spider have?",
      options: ["4", "6", "8", "10"],
