            questions.forEach((q, i) => {
              if (userAnswers[i] === q.answer){
                userAnswers++
              }
            })

  return (
    <div>
      <h2>Simple Quiz</h2>
      {
        questions.map((q, i) => {
          return (<div key={i}>
            <p>{q.question}</p>
            {
              q.options.map((opt, j) => {
                return (
                  <div key={j}>
                    <label >
                      <input type="radio" name={`q${i}`} value={j} />{opt}
                    </label>
                  </div>
                )
              })
            }
            <br />
          </div>
          )
        })
      }
      <p>You scored {result} out of {questions.length}</p>
      <button onClick={showResult}>Submit Quiz</button>
    </div>
  );
}
}

export default App;
