import React, { useState, useEffect } from 'react'

function Condition() {
    return (
        <>
            <p>Count: {count}</p>
            <button onClick={() => setIsRunning(true)}>Start</button>
            <button onClick={() => setIsRunning(false)}>Stop</button>
            <button onClick={() => setCount(0)}>Reset</button>
        </>
    );
}

export default Condition;
